const minutes = function (m) {
    return m * 1000 * 60
};

const timers = {};
const doAfter = function (delay, code, ...args) {
    clearTimeout(timers[code.name]);
    timers[code.name] = setTimeout((...params) => {
        code(...params)
    }, delay, ...args);
}



